    private void btnGrabarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGrabarActionPerformed
        
        dni [indice] = jTextFDni.getText();
        nombre [indice] = jTextFNombre.getText();
        apellido [indice] = jTextFApellido.getText();
        direccion [indice] = jTextFDireccion.getText();
        telefono [indice] = jTextFTelefono.getText();
        fechaNac [indice] = jTextFFNac.getText();
        
    }//GEN-LAST:event_btnGrabarActionPerformed

    private void btnAdelanteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAdelanteActionPerformed
        
        if(indice >= 0 && indice <9)
        {
            indice++;
            jTextFIndice.setText("" + indice);
            jTextFDni.setText(dni [indice]);
            jTextFNombre.setText(nombre [indice]);
            jTextFApellido.setText(apellido [indice]);
            jTextFDireccion.setText(direccion [indice]);
            jTextFTelefono.setText(telefono [indice]);
            jTextFFNac.setText(fechaNac [indice]);
        }
    }//GEN-LAST:event_btnAdelanteActionPerformed

    private void btnAtrasActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAtrasActionPerformed
        
        
        if(indice > 0 && indice <=9)
        {
            indice--;
            jTextFIndice.setText("" + indice);
            jTextFDni.setText(dni [indice]);
            jTextFNombre.setText(nombre [indice]);
            jTextFApellido.setText(apellido [indice]);
            jTextFDireccion.setText(direccion [indice]);
            jTextFTelefono.setText(telefono [indice]);
            jTextFFNac.setText(fechaNac [indice]);
        }
    }//GEN-LAST:event_btnAtrasActionPerformed
    
